import {API} from "../../backend";

export const getProduct = async (productId) => {
    try {
        const response = await fetch(`${API}/product/${productId}`, {
            method: "GET",
            headers: {
                Accept: "application/json"
            }
        }) 

        return console.log(response.json());
    } catch (error) {
        return console.log(error)
    }
}
