import React, { useState, useEffect } from "react";
import ViewIcon from "../icons/view.svg";
import EditIcon from "../icons/Edit.svg";
import AddIcon from "../icons/add.svg";
import { isAuthenticated } from "../auth";
// import { deleteemployee, getAllemployees } from "../core/helper/employeeDetailHelper";
import { API } from "../backend";
import { getEmployees } from "../user";
// import Addemployee from "./Addemployee";
// import employeeDetail from "./employeeDetail";

const Employee = () => {
  const [employees, setEmployees] = useState([]);
  const [status, setStatus] = useState("all");
  const [addEmployeeActive, setAddEmployeeActive] = useState("");
  const [employeeDetail, setEmployeeDetail] = useState("");
  const [employee, setEmployee] = useState();

  const { user, token } = isAuthenticated();

  const loadEmployees = async (userId, token) => {
    try {
      const data = await getEmployees();
      if (data.error) {
        return console.log(data.error);
      } else {
        return setEmployees(data);
      }
    } catch (error) {
      return console.log(error);
    }
  };

  const handlePreview = async (employee) => {
    return setEmployeeDetail("employeeDetail"), setEmployee(employee)
  };

  const handleEdit = async (employee) => {
    //
  };

  const handleDelete = async (employee) => {
    // try {
    //   const data = await deleteemployee(user._id, token, employee._id);
    //   if(data.error){
    //     return console.log(data.error);
    //   }else{
    //     return loademployees(category);
    //   }
    // } catch (error) {
    //   return console.log(error);
    // }
  };

  useEffect(() => {
    loadEmployees(user._id, token);
  }, [employee]);
  return (
    <section className="adminDashPanel-section employee-section">
      <h1 className="adminDashPanel-right-header">Employees</h1>
      <div className="adminDashPanel-right-subsection adminDashPanel-employee-add-btn-subSection">
        <button className="adminDashPanel-employee-add-btn" onClick={() => setAddEmployeeActive("addEmployee")} ><img src={AddIcon} className="adminDashPanel-employee-add-btn-icon" alt="" />ADD EMPLOYEE</button>
      </div>
      <div className="adminDashPanel-right-subsection adminDashPanel-employee-filter-subSection">
        <button
          className={`adminDashPanel-employee-employee-btn ${
            status === "all" ? "active" : ""
          }`}
          onClick={() => setStatus("all")}
        >
          All
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "Available" ? "active" : ""
          }`}
          onClick={() => setStatus("Available")}
        >
          Available
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "NotAvailable" ? "active" : ""
          }`}
          onClick={() => setStatus("NotAvailable")}
        >
          Not Available
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "OnDuty" ? "active" : ""
          }`}
          onClick={() => setStatus("OnDuty")}
        >
          On Duty
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "Deleted" ? "active" : ""
          }`}
          onClick={() => setStatus("Deleted")}
        >
          Deleted
        </button>
      </div>
      <div className="adminDashPanel-right-subsection">
        <table className="adminDashPanel-right-table">
          <thead className="adminDashPanel-right-table-head-sec">
            <tr>
              <th className="adminDashPanel-right-table-head-value">
                Name
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Email
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Phone Number
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Status
              </th>

              <th className="adminDashPanel-right-table-head-value">
                Address
              </th>

              <th className="adminDashPanel-right-table-head-value">Created At</th>
              <th className="adminDashPanel-right-table-head-value">Action</th>
            </tr>
          </thead>
          <tbody className="adminDashPanel-right-table-body-sec">
            {employees &&
              employees.map((employee, index) => {
                return (
                  <tr
                    key={index}
                    className="adminDashPanel-right-table-body-tr"
                  >
                    <td className="adminDashPanel-right-table-body-value">
                    {employee.Name}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {employee.pName}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {employee._id}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {employee.pDescription}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {employee.pCategory}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {employee.pStock} Kg
                    </td>

                    <td className="adminDashPanel-right-table-body-value">
                      {employee.pPrice}
                    </td>
                    {/* <td className="adminDashPanel-right-table-body-value">
                      {moment(employee.createdAt).format("DD-MMM-yyyy")}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {moment(employee.updatedAt).format("DD-MMM-yyyy")}
                    </td> */}
                    <td className="adminDashPanel-right-table-body-value">
                      <button onClick={() => handlePreview(employee)}>
                        <img
                          src={ViewIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                      <button onClick={() => handleEdit(employee)}>
                        <img
                          src={EditIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                      <button onClick={() => handleDelete(employee)}>
                        <img
                          src={EditIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>

      {/* {addemployeeActive === "addemployee" && <Addemployee setAddemployeeActive={setAddemployeeActive}/>}
      {employeeDetail === "employeeDetail" && <employeeDetail setemployeeDetail= {setemployeeDetail} employee={employee} />} */}

    </section>
  );
};

export default Employee;
