import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { isAuthenticated } from "../auth";
import { getUserCart } from "../user";
import CartItem from "./CartItem";

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [subTotal, setSubTotal] = useState({
    subTotal_items: "",
    subTotal_value: 0,
  });

  const { subTotal_items, subTotal_value } = subTotal;

  const { userId } = useParams();

  const { user, token } = isAuthenticated();

  const preLoad = async (userId, token) => {
    try {
      const response = await getUserCart(userId, token);
      return setCart(response.cart);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    preLoad(userId, token);
  }, []);

  var total = 0;
  cart.map(
    (cartItem) => (total = total + cartItem.product.pPrice * cartItem.quantity)
  );

  // setSubTotal({
  //   ...subTotal,
  //   subTotal_items: cart.length,
  //   subTotal_value: total,
  // });

  return (
    <section className="cart-section">
      <div className="wrap cart-wrap">
        <div className="cart-header-sec">
          <h1 className="cart-header">My Cart </h1>
        </div>
        <div className="cart-subsection">
          <div className="cartDetail-sec">
            <div className="cartDetail-sec-hr"></div>
            {cart &&
              cart.map((cartItem, index) => {
                return <CartItem cartItem={cartItem} key={index} />;
              })}
              <div className="cart-subTotal-sec">
            <h2 className="cart-subTotal-h2">{`Subtotal (${subTotal_items} items) : ${subTotal_value}`}</h2>
          </div>
          </div>
          <div className="cart-placeOrder-sec"></div>
        </div>
      </div>
    </section>
  );
};

export default Cart;
