import React,{useState, useEffect} from 'react';
import moment from 'moment';
import { getAllOrders } from '../user';
import { isAuthenticated } from '../auth';
import ViewIcon from "../icons/view.svg";
import EditIcon from "../icons/Edit.svg";


const Order = () => {
  const [orders, setOrders] =useState([]);

  const {user, token} = isAuthenticated();

  const loadOrders = async (userId, token, status) => {
    try {
      const data = await getAllOrders(userId, token, status);
      if (data.error) {
        return console.log(data.error);
      } else {
        return setOrders(data);
      }
    } catch (error) {
      return console.log(error);
    }
  };

  useEffect(() => {
    loadOrders(user._id, token, "all")
  },[])
  return (
    <section className="adminDashPanel-right-section order-section">
    <h1 className="adminDashPanel-right-header">Orders</h1>
      <div className="adminDashPanel-right-subsection">
        <table className="adminDashPanel-right-table">
          <thead className="adminDashPanel-right-table-head-sec">
            <tr>
              <th className="adminDashPanel-right-table-head-value">Order ID</th>
              <th className="adminDashPanel-right-table-head-value">Status</th>
              <th className="adminDashPanel-right-table-head-value">Total</th>
              <th className="adminDashPanel-right-table-head-value">Ordered On</th>
              <th className="adminDashPanel-right-table-head-value">Payment Mode</th>
              <th className="adminDashPanel-right-table-head-value">
                Payment Status
              </th>
              <th className="adminDashPanel-right-table-head-value">Delivery Boy</th>
              <th className="adminDashPanel-right-table-head-value">Action</th>
            </tr>
          </thead>
          <tbody className="adminDashPanel-right-table-body-sec">
            {orders &&
              orders.map((order, index) => {
                return (
                  <tr key={index} className="adminDashPanel-right-table-body-tr ">
                    <td className="adminDashPanel-right-table-body-value">
                      {order._id}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      <div
                        className={`adminDashPanel-right-table-body-div ${order.Ostatus}`}
                      >
                        {order.Ostatus}
                      </div>
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {order.OtotalPrice}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {moment(order.createdAt).format("DD-MMM-yyyy")}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {order.OpaymentMode}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      <div className={`adminDashPanel-right-table-body-div ${order.OpaymentStatus}`}>
                      {order.OpaymentStatus}
                      </div>
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {order.OemployeeId ? order.OemployeeId : "Not Assigned"}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                    <button><img src={ViewIcon} alt="" className="adminDashPanel-right-table-icon vie" /></button>
                      <button><img src={EditIcon} alt="" className="adminDashPanel-right-table-icon vie" /></button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
      </section>
  )
}

export default Order
