import React, {useState} from "react";
import { useParams } from "react-router-dom";

const Settings = () => {

  const {userId} = useParams();

  const [values, setValues] = useState({
    oldPassword: "",
    newPassword: "",
    confirmPassword: ""
  })

  const {oldPassword, newPassword, confirmPassword} = values;

  const handleChange = (name) => (event) => {
    setValues({ ...values, [name]: event.target.value });
    setValues({ ...values, loading: "loading" });
  };

  const onSubmit = async (event) => {
    event.preventDefault();

    try {
        
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <section className="userBoard-right-section accounts-section">
      <h1 className="userBoard-right-header">Settings</h1>
      <div className="userBoard-right-subSec">
        <div className="userBoard-right-subHead">
          <h2 className="userBoard-subHeader">Change Password</h2>
        </div>
        <div className="userBoard-right-subInner">
          <div className="userBoard-right-single-group">
            <div className="userBoard-right-group">
              <label className="userBoard-right-label">Old Password</label>
             <input type="password" className="userBoard-right-input" onChange={() => handleChange('oldPassword')} value= {oldPassword} />
            </div>
          </div>
          <div className="userBoard-right-single-group">
            <div className="userBoard-right-group">
              <label className="userBoard-right-label">New Password</label>
             <input type="password" className="userBoard-right-input" onChange={() => handleChange('newPassword')} value= {newPassword} />
            </div>
          </div>
          <div className="userBoard-right-single-group">
            <div className="userBoard-right-group">
              <label className="userBoard-right-label">Confirm Password</label>
             <input type="password" className="userBoard-right-input" onChange={() => handleChange('confirmPassword')} value= {confirmPassword} />
            </div>
          </div>
          <button className="userBoard-right-btn" onClick={() => onSubmit()} >Change</button>
        </div>
      </div>
    </section>
  );
};

export default Settings;
