import React, { useState, useEffect } from "react";
import { API } from "../backend";
import { getAllProducts } from "./helper/shopHelper";

const Shop = () => {

  const [products, setProducts] = useState([]);

  const loadAllProducts = () => {
    getAllProducts()
      .then((data) => {
        console.log(data);
        return setProducts(data);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    loadAllProducts();
  }, []);

  return (
    <section className="shop-section">
      <div className="wrap hero-wrap">
        <h1>This is hot hero section</h1>
      </div>

      <div className="wrap products-wrap">
        {products.map((product, index) => {
          return(
            <div className="product-sec" key={index}>
              <div className="product-category-sec">
                {/* <p className="product-category">{}</p> */}
              </div>
              <img src={`${API}/product/photo/${product._id}`} className="product-img" />
              <h2 className="product-name">{product.pName}</h2>
              <h3 className="product-stock">{product.pStock} Kg in stock</h3>
              <h1 className="product-price">{product.pPrice}/Kg</h1>
            </div>
          )
        } )}
      </div>
    </section>
  );
};

export default Shop;
