import { API } from "../../backend";

export const getProduct = (productId) => {
    return fetch(`${API}/product/${productId}`, {
        method: "GET",
        headers: {
            Accept: "application/json"
        }
    })
    .then((response) => {
        console.log(response.json())
        return response.json();
    })
    .catch((error) => console.log(error))
}
