import { API } from "../../backend";
const axios = require('axios');

// getProduct
export const getProduct = (productId) => {
    return axios.get(`${API}/product/${productId}`)
    .then((response) => {
      console.log(response.data);
      return response.data;
    })
    .catch((error) => {
      console.log(error);
    })
}
