import React,{useState, useEffect} from 'react';
import moment from 'moment';
import { getAllOrders } from '../user';
import { isAuthenticated } from '../auth';


const Order = () => {
  const [orders, setOrders] =useState([]);

  const {user, token} = isAuthenticated();

  const loadOrders = async (userId, token, status) => {
    try {
      const data = await getAllOrders(userId, token, status);
      if (data.error) {
        return console.log(data.error);
      } else {
        return setOrders(data);
      }
    } catch (error) {
      return console.log(error);
    }
  };

  useEffect(() => {
    loadOrders(userId, token, "all")
  },[])
  return (
    <section className="adminDashPanel-right-section order-section">
    <h1 className="adminDashPanel-right-header">Pending Orders</h1>
      <div className="adminDashPanel-right-subsection">
        <table className="order-order-table">
          <thead className="order-order-table-head-sec">
            <tr>
              <th className="order-order-table-head-value">Order ID</th>
              <th className="order-order-table-head-value">Status</th>
              <th className="order-order-table-head-value">Total</th>
              <th className="order-order-table-head-value">Ordered On</th>
              <th className="order-order-table-head-value">Payment Mode</th>
              <th className="order-order-table-head-value">
                Payment Status
              </th>
              <th className="order-order-table-head-value">Delivery Boy</th>
              <th className="order-order-table-head-value">Action</th>
            </tr>
          </thead>
          <tbody className="order-order-table-body-sec">
            {orders &&
              orders.map((order, index) => {
                return (
                  <tr key={index} className="order-order-table-body-tr ">
                    <td className="order-order-table-body-value">
                      {order._id}
                    </td>
                    <td className="order-order-table-body-value">
                      <div
                        className={`order-order-table-body-div ${order.Ostatus}`}
                      >
                        {order.Ostatus}
                      </div>
                    </td>
                    <td className="order-order-table-body-value">
                      {order.OtotalPrice}
                    </td>
                    <td className="order-order-table-body-value">
                      {moment(order.createdAt).format("DD-MMM-yyyy")}
                    </td>
                    <td className="order-order-table-body-value">
                      {order.OpaymentMode}
                    </td>
                    <td className="order-order-table-body-value">
                      <div className={`order-order-table-body-div ${order.OpaymentStatus}`}>
                      {order.OpaymentStatus}
                      </div>
                    </td>
                    <td className="order-order-table-body-value">
                      {order.OemployeeId ? order.OemployeeId : "Not Assigned"}
                    </td>
                    <td className="order-order-table-body-value">
                      <button>Preview</button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
      </section>
  )
}

export default Order
