import React, {useState, useEffect} from "react";
import Accounts from "./Accounts";

const CustomerBoard = () => {
    const [tabActive, setTabActive] = useState(null);

    console.log("tabActive : ", tabActive);

    const handleChange = (tab) => {
      setTabActive(tab);
    }



    // useEffect(() => {
    //     setTabActive(currentTab)
    // }, [])

  return (
    <section className="userBoard-section">
      <div className="userBoard-left">
        <div className="userBoard-user-detail"></div>
        <ul className="userBoard-left-ul">
            <li className="userBoard-left-li" onClick={() => handleChange('orders')} >My Orders</li>
            <li className="userBoard-left-li" onClick={() => handleChange('accounts')} >My Account</li>
            <li className="userBoard-left-li" onClick={() => handleChange('orders')} >My Settings</li>
        </ul>
      </div>
      <div className="userBoard-right">
        {tabActive== "accounts" && <Accounts />}
        {tabActive== "orders" && <Accounts />}
        {tabActive== "settings" && <Accounts />}
      </div>
    </section>
  );
};

export default CustomerBoard;
