import {API} from "../../backend";


// get Products
export const getAllProducts = () => {
    console.log(`API: ${API}`);
    return fetch(`${API}/products`, {
    // return fetch(`http://localhost:8000/api/products`, {
        method: 'GET',
        headers: {
            Accept: "application/json"
        }
    })
    .then((response) => {
        return response.json();
    })
    .catch((error) => console.log(error));
}
