  //           productDetails && (
  //             <tr key={index}>
  //               <td className="popup-table-body-value">
  //                 {productDetail.pName}
  //               </td>
  //               <td className="popup-table-body-value">
  //                 {productDetail.pCategory}
  //               </td>
  //               <td className="popup-table-body-value">{product.quantity}</td>
  //               <td className="popup-table-body-value">
  //                 {productDetail.pPrice}
  //               </td>
  //               <td className="popup-table-body-value">
  //                 {productDetail.pPrice * product.quantity}
  //               </td>
  //             </tr>
  //           )
  //         );
  //       } else {
  //         return "";
  //       }
  //     });
  //   });
  // };

  // useEffect(() => {
