import React,{useState} from "react";
import { Link, useNavigate } from "react-router-dom";
import { isAuthenticated } from "../auth/index";

const Nav = () => {
  const [active, setActive] = useState(false);

  const showDropDown = () => {
    setActive(true);
  }

  const hideDropDown = () => {
    setActive(false);
  }

  return (
    <section className="nav-section">
      <div className="wrap nav-wrap">
        <div className="nav-left-sec">
          <ul className="nav-ul">
            <li className="nav-li">
              <Link className="nav-link" to="/">
                Shop
              </Link>
            </li>
            <li className="nav-li">
              <Link className="nav-link" to="/">
                Contact Us
              </Link>
            </li>
          </ul>
        </div>
        <div className="nav-logo-sec">
          <Link className="nav-logo" to="/">
            fresh from farm
          </Link>
        </div>
        <div className="nav-right-sec">
          {!isAuthenticated() && (
            <ul className="nav-ul">
              <li className="nav-li">
                <button className="nav-btn">Sign Up</button>
              </li>
              <li className="nav-li nav-border">
                <button className="nav-btn">Log In</button>
              </li>
            </ul>
          )}

          {isAuthenticated() && isAuthenticated().user.role === 0 && (
            <ul className="nav-ul">
              <li className="nav-li">
                <button className="nav-btn">Cart</button>
              </li>
              <li className="nav-li nav-border">
                <button className="nav-btn">{isAuthenticated().user.name.length > 6 ? (isAuthenticated().user.name).substring(0,6) : isAuthenticated().user.name}</button>
                <ul className= "nav-drop-ul">
                  <li className= "nav-drop-li">My Orders</li>
                  <li className= "nav-drop-li">My Accounts</li>
                  <li className= "nav-drop-li">My Settings</li>
                  <li className= "nav-drop-li">
                    <button className= "nav-drop-btn">Log Out</button>
                  </li>
                </ul>
              </li>
            </ul>
          )}

          {isAuthenticated() && isAuthenticated().user.role === 2 && (
            <ul className="nav-ul">
              <li className="nav-li nav-border">
                <button className="nav-btn">{isAuthenticated().user.name.length > 6 ? (isAuthenticated().user.name).substring(0,6) : isAuthenticated().user.name}</button>
              </li>
            </ul>
          )}

          {isAuthenticated() && isAuthenticated().user.role === 1 && (
            <ul className="nav-ul">
              <li className="nav-li nav-border">
                <button className="nav-btn">{isAuthenticated().user.name.length > 6 ? (isAuthenticated().user.name).substring(0,6) : isAuthenticated().user.name}</button>
              </li>
            </ul>
          )}
        </div>
      </div>
    </section>
  );
};

export default Nav;
