import React, {useState, useEffect} from 'react'
import { isAuthenticated } from '../auth';
import { getCustomers } from '../user';

const Customer = () => {
  const [customers, getCustomers] = useState([]);

  const { user, token } = isAuthenticated();

  const loadEmployees = async (userId, token) => {
    try {
      const data = await getCustomers(userId, token);
      if (data.error) {
        return console.log(data.error);
      } else {
        return setEmployees(data);
      }
    } catch (error) {
      return console.log(error);
    }
  };

  const handlePreview = (employee) => {
    return setEmployeeDetail("employeeDetail"), setEmployee(employee);
  };

  const handleDelete = async (employee) => {
    try {
      const data = await deleteEmployee(user._id, token, employee._id);
      if (data.error) {
        return console.log(data.error);
      } else {
        return loadEmployees(user._id, token, status);
      }
    } catch (error) {
      return console.log(error);
    }
  };
  return (
    <section className="adminDashPanel-section customer-section">
      <h1 className="adminDashPanel-right-header">Customers</h1>
      <div className="adminDashPanel-right-subsection">
        <table className="adminDashPanel-right-table">
          <thead className="adminDashPanel-right-table-head-sec">
            <tr>
              <th className="adminDashPanel-right-table-head-value">Name</th>
              <th className="adminDashPanel-right-table-head-value">Customer ID</th>
              <th className="adminDashPanel-right-table-head-value">Email</th>
              <th className="adminDashPanel-right-table-head-value">Phone Number</th>
              <th className="adminDashPanel-right-table-head-value">Address</th>
              <th className="adminDashPanel-right-table-head-value">
                Created At
              </th>
              <th className="adminDashPanel-right-table-head-value">Action</th>
            </tr>
          </thead>
          <tbody className="adminDashPanel-right-table-body-sec">
            {customers &&
              customers.map((customer, index) => {
                return (
                  <tr
                    key={index}
                    className="adminDashPanel-right-table-body-tr"
                  >
                    <td className="adminDashPanel-right-table-body-value">
                      {customer.Euser.name}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {customer._id}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {customer.Euser.email}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {customer.Estatus}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {customer.Euser.phoneNumber}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {customer.Euser.address.houseName}
                      <br />
                      {customer.Euser.address.streetName}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {moment(customer.createdAt).format("DD-MMM-yyyy")}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      <button onClick={() => handlePreview(customer)}>
                        <img
                          src={ViewIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                      <button onClick={() => handleDelete(customer)}>
                        <img
                          src={EditIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
      {addcustomerActive === "addcustomer" && (
        <Addcustomer setAddcustomerActive={setAddcustomerActive} />
      )}
      {customerDetail === "customerDetail" && (
        <customerDetail
          customer={customer}
          setcustomerDetail={setcustomerDetail}
        />
      )}
    </section>
  );
}

export default Customer
