import React, { useState, useEffect } from "react";
import ViewIcon from "../icons/view.svg";
import EditIcon from "../icons/Edit.svg";
import AddIcon from "../icons/add.svg";
import { isAuthenticated } from "../auth";
// import { deleteProduct, getAllProducts } from "../core/helper/productDetailHelper";
import { API } from "../backend";
import { getEmployees } from "../user";
// import AddProduct from "./AddProduct";
// import ProductDetail from "./ProductDetail";

const Employee = () => {
  const [employees, setEmployees] = useState([]);
  const [status, setStatus] = useState("all");
  const [addEmployeeActive, setAddEmployeeActive] = useState("");
  const [employeeDetail, setEmployeeDetail] = useState("");
  const [employee, setEmployee] = useState();

  const { user, token } = isAuthenticated();

  const loadEmployees = async (userId, token) => {
    try {
      const data = await getEmployees();
      if (data.error) {
        return console.log(data.error);
      } else {
        return setEmployees(data);
      }
    } catch (error) {
      return console.log(error);
    }
  };

  const handlePreview = async (employee) => {
    return setEmployeeDetail("employeeDetail"), setEmployee(employee)
  };

  const handleEdit = async (employee) => {
    //
  };

  const handleDelete = async (employee) => {
    // try {
    //   const data = await deleteProduct(user._id, token, product._id);
    //   if(data.error){
    //     return console.log(data.error);
    //   }else{
    //     return loadProducts(category);
    //   }
    // } catch (error) {
    //   return console.log(error);
    // }
  };

  useEffect(() => {
    loadEmployees(user._id, token);
  }, [employee]);
  return (
    <section className="adminDashPanel-section product-section">
      <h1 className="adminDashPanel-right-header">Employees</h1>
      <div className="adminDashPanel-right-subsection adminDashPanel-product-add-btn-subSection">
        <button className="adminDashPanel-product-add-btn" onClick={() => setAddEmployeeActive("addEmployee")} ><img src={AddIcon} className="adminDashPanel-employee-add-btn-icon" alt="" />ADD EMPLOYEE</button>
      </div>
      <div className="adminDashPanel-right-subsection adminDashPanel-employee-filter-subSection">
        <button
          className={`adminDashPanel-product-employee-btn ${
            status === "all" ? "active" : ""
          }`}
          onClick={() => setStatus("all")}
        >
          All
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "Available" ? "active" : ""
          }`}
          onClick={() => setStatus("Available")}
        >
          Available
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "NotAvailable" ? "active" : ""
          }`}
          onClick={() => setStatus("NotAvailable")}
        >
          Not Available
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "OnDuty" ? "active" : ""
          }`}
          onClick={() => setStatus("OnDuty")}
        >
          On Duty
        </button>
        <button
          className={`adminDashPanel-employee-filter-btn ${
            status === "Deleted" ? "active" : ""
          }`}
          onClick={() => setStatus("Deleted")}
        >
          Deleted
        </button>
      </div>
      <div className="adminDashPanel-right-subsection">
        <table className="adminDashPanel-right-table">
          <thead className="adminDashPanel-right-table-head-sec">
            <tr>
              <th className="adminDashPanel-right-table-head-value">
                Product Image
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Product Name
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Product ID
              </th>
              <th className="adminDashPanel-right-table-head-value">
                Description
              </th>

              <th className="adminDashPanel-right-table-head-value">
                Category
              </th>

              <th className="adminDashPanel-right-table-head-value">Stock</th>
              <th className="adminDashPanel-right-table-head-value">Price</th>
              <th className="adminDashPanel-right-table-head-value">Action</th>
            </tr>
          </thead>
          <tbody className="adminDashPanel-right-table-body-sec">
            {products &&
              products.map((product, index) => {
                return (
                  <tr
                    key={index}
                    className="adminDashPanel-right-table-body-tr"
                  >
                    <td className="adminDashPanel-right-table-body-value">
                      <img
                        className="adminDashPanel-product-img"
                        src={`${API}/product/photo/${product._id}`}
                        alt=""
                      />
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {product.pName}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {product._id}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {product.pDescription}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {product.pCategory}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {product.pStock} Kg
                    </td>

                    <td className="adminDashPanel-right-table-body-value">
                      {product.pPrice}
                    </td>
                    {/* <td className="adminDashPanel-right-table-body-value">
                      {moment(product.createdAt).format("DD-MMM-yyyy")}
                    </td>
                    <td className="adminDashPanel-right-table-body-value">
                      {moment(product.updatedAt).format("DD-MMM-yyyy")}
                    </td> */}
                    <td className="adminDashPanel-right-table-body-value">
                      <button onClick={() => handlePreview(product)}>
                        <img
                          src={ViewIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                      <button onClick={() => handleEdit(product)}>
                        <img
                          src={EditIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                      <button onClick={() => handleDelete(product)}>
                        <img
                          src={EditIcon}
                          alt=""
                          className="adminDashPanel-right-table-icon "
                        />
                      </button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>

      {/* {addProductActive === "addProduct" && <AddProduct setAddProductActive={setAddProductActive}/>}
      {productDetail === "productDetail" && <ProductDetail setProductDetail= {setProductDetail} product={product} />} */}

    </section>
  );
};

export default Employee;
