import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { isAuthenticated } from "../auth";
import { getUserCart } from "../user";

const Cart = () => {
  const [cart, setCart] = useState([]);
  // var cart = [];

  const { userId } = useParams();

  const { user, token } = isAuthenticated();

  const preLoad = async (userId, token) => {
    try {
      const response = await getUserCart(userId, token);
      console.log(response.cart);
      return setCart(response.cart);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    preLoad(userId, token);
  }, []);

  return (
    <section className="cart-section">
      <div className="wrap cart-wrap">
        <div className="cart-header-sec">
          <h1 className="cart-header">My Cart </h1>
        </div>
        <div className="cartDetail-sec">
          <hr />
          {cart &&
            cart.map((cartItem, index) => {
              return (
                <div className="cartDetail-item" key={index}>
                  <img src="" alt="" className="cartDetail-item-img" />
                  <div className="cartDetail-item-info">
                    <h2 className="cartDetail-item-productName">
                      {cartItem.product.pName}
                    </h2>
                    {/* <div
                      className={`productDetail-category-sec ${
                        cartItem.product.pCategory === "vegetable"
                          ? "color-green"
                          : "color-orange"
                      }`}
                    >
                      <p className="productDetail-category">
                        {cartItem.product.pCategory === "vegetable"
                          ? "Veg"
                          : "Fruit"}
                      </p>
                    </div> */}
                    <div
                      className={`cartDetail-item-category-sec ${
                        cartItem.product.pCategory === "vegetable"
                          ? "color-green"
                          : "color-orange"
                      }`}
                    >
                      <p className="cartDetail-item-category">
                        {cartItem.product.pCategory === "vegetable"
                          ? "Veg"
                          : "Fruit"}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
        </div>
        <div className="cart-placeOrder-sec"></div>
      </div>
    </section>
  );
};

export default Cart;
