import React, { useState, useEffect } from "react";
// import { API } from "../backend";
import { getAllProducts } from "./helper/shopHelper";

const Shop = () => {

  const [products, setProducts] = useState([]);

  const loadAllProducts = () => {
    getAllProducts()
      .then((data) => {
        console.log(data);
        return setProducts(data);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    loadAllProducts();
  }, []);

  return (
    <section className="shop-section">
      <div className="wrap hero-wrap">
        <h1>This is hot hero section</h1>
      </div>

      <div className="wrap products-wrap">
        {products.map((product, index) => {
          const productImg = product.pImg;
          return(
            <div className="product sec" key={index}>
              {/* <img src={productImg} alt="" className="product-img" /> */}
              <img src={`data:image/jpeg;base64,${product.pImg.data}`} />
              <h2 className="productName">{product.pName}</h2>
              <h3 className="productStock">{product.pStock} Kg in stock</h3>
              <h1 className="productPrice">{product.pPrice}/Kg</h1>
            </div>
          )
        } )}
      </div>
    </section>
  );
};

export default Shop;
