import { API } from "../backend";

export const getUser = async (userId, token) => {
    try {
       const result = await fetch(`${API}/user/${userId}`, {
           method: "GET",
           headers: {
               Accept: "application/json",
               Authorization: `Bearer ${token}`
           }
       })
       .then(res => res.json())

       return result;
    } catch (error) {
        console.log(error);
    }
}
