import React, { useState, useEffect } from "react";
import Cross from "../icons/cross-black.svg";

const OrderUpdate = ({ setOrderUpdateActive, order }) => {
  const [status, setStatus] = useState(order.Ostatus);

  const isSelected = (value) => {
    if (status === value) {
      return value;
    } else {
      return "";
    }
  };

  console.log("status", status);

  const onSubmit = async () => {};
  return (
    <section className="orderDetails-section">
      <div className="black-background">
        <div className="popup-small-sec">
          <div className="popup-group">
            <div className="popup-head-sec">
              <h1 className="popup-header orderDetails-popup-header">
                Update Order Status
              </h1>
              <div
                className="cross-sec"
                onClick={() => setOrderUpdateActive(null)}
              >
                <img src={Cross} alt="" className="cross-img" />
              </div>
            </div>
            <div className="popup-form">
              <div className="popup-form-single-group">
                <div className="popup-form-group">
                  <label className="popup-form-label">Order ID</label>
                  <p className="popup-form-value">{order._id}</p>
                </div>
              </div>
              <div className="popup-form-single-group">
                <div className="popup-form-group">
                  <label className="popup-form-label">Order Status</label>
                  <select
                    name="orderStatus"
                    id="orderStatus"
                    onChange={(e) => setStatus(e.target.value)}
                  >
                    <option
                      value="Ordered"
                      defaultValue={isSelected("Ordered")}
                    >
                      Ordered
                    </option>
                    <option
                      value="Not-Confirmed"
                      defaultValue={isSelected("Not-Confirmed")}
                    >
                      Not-Confirmed
                    </option>
                    <option
                      value="Processing"
                      defaultValue={isSelected("Processing")}
                    >
                      Processing
                    </option>
                    <option
                      value="Picking-Up"
                      defaultValue={isSelected("Picking-Up")}
                    >
                      Picking-Up
                    </option>
                    <option
                      value="Out-For-Delivery"
                      defaultValue={isSelected("Out-For-Delivery")}
                    >
                      Out-For-Delivery
                    </option>
                    <option
                      value="Delivered"
                      defaultValue={isSelected("Delivered")}
                    >
                      Delivered
                    </option>
                    <option
                      value="Cancelled"
                      defaultValue={isSelected("Cancelled")}
                    >
                      Cancelled
                    </option>
                  </select>
                  {status}
                </div>
              </div>
              <button className="popup-form-btn" onClick={onSubmit}>
                Update
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default OrderUpdate;
